#import "ContainsBlock.h"

@implementation ContainsBlock

@synthesize block = _block;
@synthesize str;

- (id)init {
    if ((self = [super init])) {
        
        self.str = @"STR";
        __weak ContainsBlock *weakSelf = self;
        //__block ContainsBlock *blockSelf = self; // to fix use this.
        self.block = ^{
//            NSLog(@"object is %@", self); // self retain cycle
//            self.str = @"SDF";
            weakSelf.str = @"";
        };
        
        dispatch_async(dispatch_get_main_queue(), ^{
            self.str = @"ABCD";
        });
    }
    return self;
}

- (void)dealloc {
    self.block = nil;
    NSLog (@"ContainsBlock"); // never called.
//    [super dealloc];
}

- (void)callblock {
    self.block();
}

@end

