#import "ViewController2.h"
#import "ContainsBlock.h"

@interface ViewController2 ()

//@property (nonatomic, weak) NSMutableArray *byteArray; vs @property (nonatomic, strong) NSMutableArray *byteArray;
// Weak will disappear when view disappears, strong will stay with view until dealloc is called

@property (nonatomic, strong) NSMutableArray *byteArray;

@end

@implementation ViewController2

// block that returns int and takes a string param
int (^simpleBlock)(NSString *);

- (void)viewDidLoad {
    [super viewDidLoad];
    // Do any additional setup after loading the view.
    
    self.byteArray = [NSMutableArray new];
    
    [[self view] setBackgroundColor:[UIColor blueColor]];
//    ContainsBlock *containsBlock = [[ContainsBlock alloc] init];
    [self allocateMemoryOfSize:4000000];
    [self setupStuff];
    
    [self doSomething];
}

- (void)didReceiveMemoryWarning {
    [super didReceiveMemoryWarning];
    // Dispose of any resources that can be recreated.
}

- (void)dealloc {
    NSLog(@"VC2 dealloc");
}

- (void)setupStuff {
    self.str = @"STR";
    
    simpleBlock = ^int (NSString *stringVal) {
        NSLog(@"In simple Block");
        return [stringVal intValue];
    };
    
//    simpleBlock = ^{
//        NSLog(@"In simple Block");
//    };
    
//    void (^simpleBlock)(void) = ^{
//        NSLog(@"This is a block");
//    };
    
    __weak ViewController2 *weakSelf = self;
    //__block ContainsBlock *blockSelf = self; // to fix use this.
    self.block = ^{
//        NSUInteger num = 180;
        NSLog(@"Display nothing %@", weakSelf.str);
        //            NSLog(@"object is %@", self); // self retain cycle
        //            self.str = @"SDF";
        weakSelf.str = @"DSFDSF";
//        self.str = @"";
    };
    
    dispatch_async(dispatch_get_main_queue(), ^{
        self.str = @"ABCD";
    });

}

- (void)doSomething {
//    simpleBlock();
    int num = simpleBlock(@"47");
    NSLog(@"Num: %d", num);
}

- (void)allocateMemoryOfSize:(NSUInteger)numberOfBytes {
    
    for (int i = 0; i < numberOfBytes; i++) {
        [self.byteArray addObject:[NSNumber numberWithInt:(i % 7)]];
    }
}

